
function generateCertificate() {
    const name = document.getElementById("name").value;
    const start = new Date(document.getElementById("start").value);
    const end = new Date(document.getElementById("end").value);
    const domain = document.getElementById("domain").value;
    const gender = document.getElementById("gender").value;

    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    const formattedStart = start.toLocaleDateString("en-US", options);
    const formattedEnd = end.toLocaleDateString("en-US", options);
    const today = new Date().toLocaleDateString("en-US", options);

    document.getElementById("cert-date").textContent = today;
    document.getElementById("cert-name").textContent = `Ms. ${name}`;
    document.getElementById("cert-domain").textContent = domain;
    document.getElementById("cert-start").textContent = formattedStart;
    document.getElementById("cert-end").textContent = formattedEnd;

    const pronoun = gender === "male" ? "him" : "her";
    const pronoun2 = gender === "male" ? "his" : "her";

    document.getElementById("cert-description").textContent =
        `We found ${gender === "male" ? "him" : "her"} to be sincere, hardworking, dedicated and result-oriented, while working well as a part of the team. We take this opportunity to congratulate ${gender === "male" ? "him" : "her"} for completing the internship.`;
    document.getElementById("cert-pronoun").textContent = pronoun.charAt(0).toUpperCase() + pronoun.slice(1);
    document.getElementById("cert-pronoun2").textContent = pronoun2;

    const certificate = document.getElementById("certificate");
    certificate.style.display = "block";

    const opt = {
        filename:     `${name}-Internship-Certificate.pdf`,
        image:        { type: 'jpeg', quality: 0.98 },
        html2canvas:  { scale: 2 },
        jsPDF:        { unit: 'in', format: 'letter', orientation: 'portrait' }
    };

    html2pdf().set(opt).from(certificate).save().then(() => {
        certificate.style.display = "none";
    });
}


document.getElementById('proficiency').addEventListener('change', function () {
  const badgeImage = document.getElementById('badge-image');
  const selected = this.value;

  let imagePath = '';

  switch (selected) {
    case 'professional':
      imagePath = 'data:image/png;base64,'; 
      break;
    case 'working':
      imagePath = 'data:image/png;base64,';
      break;
    case 'fundamental':
      imagePath = 'data:image/png;base64,';
      break;
    
  }

  if (imagePath) {
    badgeImage.src = imagePath;
    badgeImage.style.display = 'block';
  } else {
    badgeImage.style.display = 'none';
  }
});
